import{_ as t,c as o,a5 as a,o as d}from"./chunks/framework.B5CpDqM0.js";const h=JSON.parse('{"title":"Android Debug Bridge ⚙️","description":"","frontmatter":{"authors":"ShutdownRepo"},"headers":[],"relativePath":"mobile-apps/android/android-debug-bridge.md","filePath":"mobile-apps/android/android-debug-bridge.md","lastUpdated":1724982529000}'),r={name:"mobile-apps/android/android-debug-bridge.md"};function i(n,e,s,l,c,u){return d(),o("div",null,e[0]||(e[0]=[a('<h1 id="android-debug-bridge-⚙️" tabindex="-1">Android Debug Bridge ⚙️ <a class="header-anchor" href="#android-debug-bridge-⚙️" aria-label="Permalink to &quot;Android Debug Bridge ⚙️&quot;">​</a></h1><h2 id="theory" tabindex="-1">Theory <a class="header-anchor" href="#theory" aria-label="Permalink to &quot;Theory&quot;">​</a></h2><p>Android Debug Bridge (adb) is a versatile command-line tool that lets you communicate with a device. The adb command facilitates a variety of device actions, such as installing and debugging apps, and it provides access to a Unix shell that you can use to run a variety of commands on a device.</p><h2 id="practical" tabindex="-1">Practical <a class="header-anchor" href="#practical" aria-label="Permalink to &quot;Practical&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Command</th><th>Description</th></tr></thead><tbody><tr><td><code>adb devices</code></td><td>List connected devices</td></tr><tr><td><code>adb shell</code></td><td>shell on connected phone (then su for root)</td></tr><tr><td><code>adb install &quot;app.apk&quot;</code></td><td>install app on your phone</td></tr><tr><td><code>adb backup </code></td><td>backup \\</td></tr><tr><td><code>adb logcat</code></td><td>logs on your phone</td></tr></tbody></table><h2 id="frequent-problems" tabindex="-1">Frequent problems <a class="header-anchor" href="#frequent-problems" aria-label="Permalink to &quot;Frequent problems&quot;">​</a></h2><p>Sometimes ADB can no longer connect to the mobile. There are several reasons for this.</p><h3 id="make-sure-developers-options-and-usb-debugging-are-activated" tabindex="-1">Make sure Developers options and USB debugging are activated <a class="header-anchor" href="#make-sure-developers-options-and-usb-debugging-are-activated" aria-label="Permalink to &quot;Make sure Developers options and USB debugging are activated&quot;">​</a></h3><ul><li>Enable Developers options</li></ul><ol><li>Go to the settings of the Android device.</li><li>Go to the system settings.</li><li>Go to &quot;About the phone&quot;.</li><li>Tap 7 times &quot;build number&quot;.</li><li>The options for developers appear in the system settings.</li></ol><ul><li>Inside Developers options, enable USB Debugging</li></ul><blockquote><p>When in doubt, reboot (Confucious)</p></blockquote><h2 id="resources" tabindex="-1">Resources <a class="header-anchor" href="#resources" aria-label="Permalink to &quot;Resources&quot;">​</a></h2><p><a href="https://developer.android.com/studio/debug/dev-options" target="_blank" rel="noreferrer">https://developer.android.com/studio/debug/dev-options</a></p><p><a href="https://developer.android.com/studio/command-line/adb" target="_blank" rel="noreferrer">https://developer.android.com/studio/command-line/adb</a></p>',15)]))}const b=t(r,[["render",i]]);export{h as __pageData,b as default};
