import{_ as e,c as s,a5 as i,o as t}from"./chunks/framework.B5CpDqM0.js";const c=JSON.parse('{"title":"Magisk","description":"","frontmatter":{"authors":"ShutdownRepo"},"headers":[],"relativePath":"mobile-apps/android/magisk.md","filePath":"mobile-apps/android/magisk.md","lastUpdated":1724982529000}'),r={name:"mobile-apps/android/magisk.md"};function l(o,a,n,h,p,d){return t(),s("div",null,a[0]||(a[0]=[i('<h1 id="magisk" tabindex="-1">Magisk <a class="header-anchor" href="#magisk" aria-label="Permalink to &quot;Magisk&quot;">​</a></h1><h2 id="theory" tabindex="-1">Theory <a class="header-anchor" href="#theory" aria-label="Permalink to &quot;Theory&quot;">​</a></h2><blockquote><p>Magisk is a suite of open source software for customizing Android, supporting devices higher than Android 6.0.<br> Some highlight features:</p><ul><li>MagiskSU: Provide root access for applications</li><li>Magisk Modules: Modify read-only partitions by installing modules</li><li>MagiskBoot: The most complete tool for unpacking and repacking Android boot images</li><li>Zygisk: Run code in every Android applications&#39; processes</li></ul><p>(<a href="https://github.com/topjohnwu/Magisk" target="_blank" rel="noreferrer">Magisk GitHub repo</a>)</p></blockquote><h2 id="practical" tabindex="-1">Practical <a class="header-anchor" href="#practical" aria-label="Permalink to &quot;Practical&quot;">​</a></h2><h3 id="install-magisk" tabindex="-1">Install Magisk <a class="header-anchor" href="#install-magisk" aria-label="Permalink to &quot;Install Magisk&quot;">​</a></h3><ol><li>Download latest APK release at <a href="https://github.com/topjohnwu/Magisk/releases" target="_blank" rel="noreferrer">https://github.com/topjohnwu/Magisk/releases</a></li><li>Install package on the phone</li></ol><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">adb</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;Magisk-vXX.Y.apk&quot;</span></span></code></pre></div><h3 id="install-magisk-modules" tabindex="-1">Install Magisk modules <a class="header-anchor" href="#install-magisk-modules" aria-label="Permalink to &quot;Install Magisk modules&quot;">​</a></h3><ol><li>Download ZIP module (e.g. <a href="https://github.com/NVISOsecurity/MagiskTrustUserCerts/releases" target="_blank" rel="noreferrer">MagiskTrustUserCerts</a>, <a href="https://github.com/HuskyDG/MagiskHide/releases/tag/v1.10.3" target="_blank" rel="noreferrer">MagiskHide</a>, ...)</li><li>Push the archive on the phone</li></ol><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">adb</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> push</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;module.zip&quot;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;/sdcard/Download&quot;</span></span></code></pre></div><p>Once the archive is on the phone&#39;s storage, the module can be installed and enabled within the Magisk app</p><ul><li>Magisk App --&gt; Modules --&gt; Install from storage --&gt; Reboot</li><li>After reboot, enable the package</li></ul><h2 id="references" tabindex="-1">References <a class="header-anchor" href="#references" aria-label="Permalink to &quot;References&quot;">​</a></h2><p><a href="https://github.com/topjohnwu/Magisk" target="_blank" rel="noreferrer">https://github.com/topjohnwu/Magisk</a></p><p><a href="https://github.com/NVISOsecurity/MagiskTrustUserCerts/releases" target="_blank" rel="noreferrer">https://github.com/NVISOsecurity/MagiskTrustUserCerts/releases</a></p>',15)]))}const k=e(r,[["render",l]]);export{c as __pageData,k as default};
